$(function () {
    $('input').iCheck({
      checkboxClass: 'icheckbox_square-blue',
      radioClass: 'iradio_square-blue',
      increaseArea: '20%' // optional
    });


  
    $(document).ready(function(){
      $("#clickLogin").click(()=>{
        $("#clickLogin").html('<i class="fa fa-spin fa-spinner"></i> Loading...')
        $("#clickLogin").prop('disabled',true);
          let data = {
            username : $("#Username").val(),
            password : $("#Password").val()
          }
          $.ajax({
            url : BASE_URL+"Auth/DoLogin",
            type : 'POST',
            data : data,
            success : (res)=>{
              if(res == 300){
                var url = BASE_URL+"Home";
                  window.location.replace(url);
              }else{
                bootbox.alert("Opps ! Your Email or Password is wrong.")
              }
              $("#clickLogin").html('Login')
              $("#clickLogin").prop('disabled',false)
            },
            ajaxSend: function(){
            },
            beforeSend: function(){
            },
            error: function (request, status, error) {
              bootbox.alert("Opps! Some thing wrong, Please Try again later.")
            }
          })

      })
    })


});